import { toSentenceCase } from "./toSentenceCase";

describe("toSentenceCase", () => {
  it("returns empty string if input is empty", () => {
    expect(toSentenceCase("")).toBe("");
  });

  it("returns original text if it contains '@rn-audit' (case insensitive)", () => {
    expect(toSentenceCase("@RN-AUDIT: check this")).toBe("@RN-AUDIT: check this");
    expect(toSentenceCase("please fix @rn-audit-label")).toBe("please fix @rn-audit-label");
  });

  it("converts text to sentence case if it doesn't contain '@rn-audit'", () => {
    expect(toSentenceCase("hello WORLD")).toBe("Hello world");
    expect(toSentenceCase("THIS IS A TEST")).toBe("This is a test");
  });
});
